import React from "react";
import { Box, Text, Button, Image } from "@chakra-ui/react";
import { useNavigate } from "react-router-dom";
import theme from "../theme";
import { useLoading } from "../contexts/LoadingContext";

const Landing = () => {
  const hardcodedWalletAddress = "0xYourHardcodedWalletAddressHere"; // Replace with your actual wallet address
  const { setIsLoading } = useLoading();
  const navigate = useNavigate();

  // Function to simulate wallet connection using a hardcoded wallet address
  const handleConnectWallet = async () => {
    console.log("Using hardcoded wallet address:", hardcodedWalletAddress);

    // Start the loading animation
    setIsLoading(true);

    // Simulate a delay for the wallet connection process (e.g., 1 second)
    await new Promise((resolve) => setTimeout(resolve, 1000));

    // Stop the loading animation
    setIsLoading(false);

    // Navigate to the Home page after connecting the wallet
    navigate("/home");
  };

  // Function to simulate going to the Home page (without wallet connection)
  const goHome = async () => {
    setIsLoading(true);
    await new Promise((resolve) => setTimeout(resolve, 1000));
    setIsLoading(false);
    navigate("/home");
  };

  // Function to open a pop-up tutorial window
  const openPopup = (): void => {
    window.open("/index.html#/tutorial", "_blank", "width=565,height=793");
  };

  return (
    <Box
      bg="white"
      width={theme.views.smallView.width}
      height={theme.views.smallView.height}
      display="flex"
      flexDirection="column"
      justifyContent="space-between"
      alignItems="center"
      p={4}
    >
      <Image
        src="/images/my-own-words-typing-animation.gif"
        alt="Intro GIF"
        maxW="70%"
        mb={4}
      />
      <Image src="/images/two-authors.png" alt="two kids" maxW="70%" mb={4} />

      <Text fontSize="sm" color="black" textAlign="center" mb={4}>
        Connect your wallet below to start registering your work as Intellectual
        Property.
      </Text>

      {/* Connect Wallet Button */}
      <Button
        bg="gray.800"
        color="white"
        _hover={{ bg: "gray.600" }}
        size="sm"
        width="40%"
        p={4}
        mb={2}
        onClick={handleConnectWallet} // Connect wallet with the hardcoded address
      >
        Connect Wallet
      </Button>

      {/* Go to Home Button */}
      <Button
        bg="gray.800"
        color="white"
        _hover={{ bg: "gray.600" }}
        size="sm"
        width="40%"
        p={4}
        mb={2}
        onClick={goHome}
      >
        Go to Home
      </Button>

      {/* Open Pop-up Button */}
      <Button
        bg="gray.800"
        color="white"
        _hover={{ bg: "gray.600" }}
        size="sm"
        width="40%"
        p={4}
        mb={2}
        onClick={openPopup}
      >
        Open Pop-up
      </Button>
    </Box>
  );
};

export default Landing;
